{
  "query": "mutation RegisterUser($nom: String!, $prenom: String!, $email: String!, $password: String!, $bio: String) {\n  register(nom: $nom, prenom: $prenom, email: $email, password: $password, bio: $bio) {\n    id\n    nom\n    prenom\n    email\n    bio\n  }\n}",
  "variables": {
    "nom": "Labit",
    "prenom": "Ayoub",
    "email": "ayoub@example.com",
    "password": "password123",
    "bio": "Développeur Full Stack passionné"
  }
}

{
  "query": "mutation LoginUser($email: String!, $password: String!) {\n  login(email: $email, password: $password)\n}",
  "variables": {
    "email": "ayoub@example.com",
    "password": "password123"
  }
}

{
  "query": "query GetProfile {
