}

{
  "query": "query GetProjects {
  projects {
    id
    titre
    description
    urlGit
    urlDemo
    image
    userId
  }
}"
}

{
  "query": "mutation CreateProject($titre: String!, $description: String!, $urlGit: String!, $urlDemo: String!, $image: String!, $userId: ID!) {\n  createProject(titre: $titre, description: $description, urlGit: $urlGit, urlDemo: $urlDemo, image: $image, userId: $userId) {\n    id\n    titre\n    description\n    urlGit\n    urlDemo\n    image\n    userId\n  }\n}",
  "variables": {
    "titre": "Portfolio React",
    "description": "Un portfolio moderne développé avec React et TypeScript",
    "urlGit": "https://github.com/ayoub/portfolio",
    "urlDemo": "https://ayoub-portfolio.netlify.app",
    "image": "https://example.com/portfolio-image.jpg",
    "userId": "{{userId}}"
  }
}

{
  "query": "query GetProject($id: ID!) {
  project(id: $id) {
    id
    titre
    description
    urlGit
    urlDemo
    image
    userId
  }
}",
  "variables": {
    "id": "PROJECT_ID_HERE"
  }
}

{
  "query": "mutation UpdateProject($id: ID!, $titre: String, $description: String, $urlGit: String, $urlDemo: String, $image: String) {
  updateProject(id: $id, titre: $titre, description: $description, urlGit: $urlGit, urlDemo: $urlDemo, image: $image) {
    id
    titre
    description
    urlGit
    urlDemo
    image
    userId
  }
}",
  "variables": {
    "id": "PROJECT_ID_HERE",
    "titre": "Portfolio React Updated",
    "description": "Portfolio moderne avec animations et dark mode"
  }
}

{
  "query": "mutation DeleteProject($id: ID!) {\n  deleteProject(id: $id) {\n    id\n    titre\n  }\n}",
  "variables": {
    "id": "PROJECT_ID_HERE"
  }
}

